import React, { Component } from 'react';
import axios from 'axios';
import PlantList from './PlantList'
import filters from './utils'

class Plants extends Component {
  state = { plants: [], size: [], mainenance: [], category: [] }
  componentDidMount() {
    this.fetchPlants()
  }

  fetchPlants = () => {
    axios.get('/plants.json')
    .then(response => this.setState({ plants: response.data }))
  }

  handleFilter = (type, value) => {
    const filters = this.state[type]
    const updatedFilters = filters.includes(value) 
      ? filters.filter(currentFilter => currentFilter !== value)
      : [...filters, value]
    this.setState({ [type]: updatedFilters }, () => this.fetchPlants() )
  }

  render() {
    console.log(this.props)
    const { plants } = this.state;
    return (
      <div>
        {
          filters.map(filter => <Filter {...filter} />)
        }
        <PlantList plants={plants}/>
      </div>
    )
  }
}

export default Plants;
