import React, { Component } from 'react';
import axios from 'axios';

class Plants extends Component {
  state = { 
    plants: ""
  }

  componentDidMount() {
    this.fetchPlants()
  }
  fetchPlants = () => {
    axios.get('/plants.json')
    .then(response => this.setState({ plants: response.data }))
    console.log(plants)
  }
  render() {
    const { plants } = this.state;
    return (
      <>
        <ul id="products-container">
          {
            plants.items.map((item, i) => {
              return (
                <div key={item.id}>
                  <li className="product-container">
                    {
                      plants.images.map ((image, i) => {
                        return <img src={image} alt="product" key={i}/>
                      })
                    }
                    <div className="product-details">
                      <h2>{plants.name}</h2>
                      <p>{plants.description}</p>
                    </div>
                  </li>
                  <hr/>
                </div>
              )
            })
          }
        </ul>
      </>
    )
  }
}

export default Plants;
