import React, { Component } from 'react';
import axios from 'axios';

class Plants extends Component {
  state = { 
    plants: [],
    product: { images:[] }
  }

  componentDidMount() {
    this.fetchPlants()
  }
  fetchPlants = () => {
    axios.get('/plants.json')
    .then(response => this.setState({ plants: response.data }))
  }
  render() {
    const { plants } = this.state;
    return (
      <div class="plant-comp-wrapper">
        <section id="Featured-Products" class="center products">
          <div class="shopfeatprod">
            <article class="cards">
              <div class="homefeatprod">
              {
                plants.images.map ((image, i) => {
                return <img src={image} alt="product" key={i}/>
                })
              }
                <h3 class="left">{Plant.name}</h3>
                <h4 class="right">$24</h4>
              </div>
            </article>
          </div>
          <div class="center">
            <button class="btnsecondaryhome">
                See all products
            </button>
          </div>
        </section>
      </div>

    )
  }
}

export default Plants;
